mapper.update:
UPDATE userinfo
SET password = #{password}
WHERE id = #{id};

mapper.select:
SELECT userinfo.*, user_status.frozen_reason, user_status.is_frozen FROM userinfo
JOIN user_status on userinfo.id = user_status.id
WHERE username = #{username}

mapper.update:
UPDATE userinfo
SET username = #{username},
    password = #{password},
    email = #{email},
    age = #{age}
WHERE id = #{id};
